package com.upsight.mediation.vast.util;

import android.annotation.SuppressLint;
import android.content.res.Resources;
import android.graphics.BitmapFactory;
import android.graphics.drawable.BitmapDrawable;
import android.graphics.drawable.Drawable;
import android.graphics.drawable.StateListDrawable;
import android.os.Build.VERSION;
import android.util.Base64;
import android.util.TypedValue;
import android.view.View;
import android.view.animation.AlphaAnimation;
import com.upsight.mediation.log.FuseLog;
import com.upsight.mediation.vast.activity.VASTActivity;
import java.text.ParseException;
import java.text.SimpleDateFormat;
import java.util.TimeZone;

public class Assets {
    public static final String button = "";
    public static final String downSmall = "";
    public static final String postrollButton = "";
    public static final String postrollDown = "";
    public static final String postrollUp = "";
    public static final String upSmall = "";
    public static final String xButton = "";

    public static Drawable getScaledDrawableFromBase64(Resources resources, String encodedString) {
        byte[] decodedString = Base64.decode(encodedString, 0);
        return new BitmapDrawable(resources, BitmapFactory.decodeByteArray(decodedString, 0, decodedString.length));
    }

    public static StateListDrawable getStateListDrawable(Resources resources, String asset) {
        StateListDrawable sld = new StateListDrawable();
        Drawable up = null;
        Drawable down = null;
        if (asset.equals("playerButton")) {
            up = getScaledDrawableFromBase64(resources, upSmall);
            down = getScaledDrawableFromBase64(resources, downSmall);
        } else if (asset.equals("postRoll")) {
            up = getScaledDrawableFromBase64(resources, postrollUp);
            down = getScaledDrawableFromBase64(resources, postrollDown);
        }
        sld.addState(new int[]{16842919}, down);
        sld.addState(new int[0], up);
        return sld;
    }

    public static StateListDrawable getPlayerUIButton(Resources resources) {
        StateListDrawable sld = new StateListDrawable();
        sld.addState(new int[0], getScaledDrawableFromBase64(resources, button));
        return sld;
    }

    public static StateListDrawable getPostrollButton(Resources resources) {
        StateListDrawable sld = new StateListDrawable();
        sld.addState(new int[0], getScaledDrawableFromBase64(resources, postrollButton));
        return sld;
    }

    public static Drawable getXButton(Resources resources) {
        return getScaledDrawableFromBase64(resources, xButton);
    }

    @SuppressLint({"NewApi"})
    public static void setImage(View view, Drawable bd) {
        if (VERSION.SDK_INT > 16) {
            view.setBackground(bd);
        } else {
            view.setBackgroundDrawable(bd);
        }
    }

    public static void setAlpha(View view, float alpha) {
        if (VERSION.SDK_INT < 11) {
            AlphaAnimation animation = new AlphaAnimation(alpha, alpha);
            animation.setDuration(0);
            animation.setFillAfter(true);
            view.startAnimation(animation);
            return;
        }
        view.setAlpha(alpha);
    }

    public static int convertToDps(float piksels) {
        return (int) TypedValue.applyDimension(1, piksels, VASTActivity.displayMetrics);
    }

    public static long parseOffset(String offset) {
        SimpleDateFormat sdf = new SimpleDateFormat(offset.matches("(0\\d|1[0-2]):([0-5]\\d):([0-5]\\d)\\.(\\d{1,3})?") ? "HH:mm:ss.SSS" : "HH:mm:ss");
        sdf.setTimeZone(TimeZone.getTimeZone("GMT"));
        try {
            return sdf.parse(offset).getTime();
        } catch (ParseException e) {
            FuseLog.d("VAST Utils", "Couldn't parse offset string");
            return 0;
        }
    }
}
